import React, { Component } from 'react';
import CurrentlyReading from './CurrentlyReading.js'
import WantToRead from './WantToRead.js'
import Read from './Read.js';
import Header from './Header.js';
import PropTypes from 'prop-types';

class ListBooks extends Component{
  static propTypes = {
    books: PropTypes.array.isRequired,
    getCRBooks: PropTypes.func.isRequired
  }
    render(){
      const {books,getCRBooks} = this.props

        return(


            <div className="list-books">
                <Header/>
            <div className="list-books-content">
              <div>
                <CurrentlyReading books={books} getCRBooks={getCRBooks}/>
                <WantToRead/>
                <Read/>
              </div>
            </div>
            {<div className="open-search">
              <button onClick={() => this.setState({ showSearchPage: true })}>Add a book</button>
            </div> }
          </div>
        )
    }
}
    export default ListBooks;
